import { useState } from 'react'

class WarUser {
  constructor({name, wins, battlesPlayed}) {
    this.name = name
    this.wins = wins
    this.battles = battlesPlayed
  }

  addWins(wins) {
    this.wins += wins
  }

  addBattles(battles) {
    this.battles +=  battles
  }

  get ratio() {
    return this.wins / this.battles || 0
  }
}

const processData = (items) => {
  let participants = []
  const findUser = (name) => participants.find((p) => p.name === name);
  items.forEach((war, i) => {
      war.participants.forEach((user) => {
          var userObj = findUser(user.name)
          if (!!userObj) {
              userObj.addBattles(user.battlesPlayed)
              userObj.addWins(user.wins)
          } else {
              userObj = new WarUser(user)
              participants.push(userObj)
          }
      })
  })
  return participants
}

let alt = true
const sortData = (items, key) => {
  alt != alt
  return items.sort((a, b) => alt ? a[key] - b[key] : b[key] - a[key])
}

const WarPerformance = (props) => {

  const [sort, setSort] = useState(['wins'])
  const [data, setData] = useState(processData(props.items))
  const state = 'hello'

  const sortedData = sortData(data, sort)

  return (
    <>
      <table className={'table'}>
        <thead>
          <tr className={'header'}>
            <th>Name</th>
            <th onClick={(() => setSort('wins'))}>Wins</th>
            <th onClick={(() => setSort('battles'))}>Battles</th>
            <th onClick={(() => setSort('ratio'))}>Ratio</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((p, i) => (
            <tr key={i}>
              <td>{p.name}</td>
              <td>{p.wins}</td>
              <td>{p.battles}</td>
              <td>{p.ratio}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <style jsx>{`
        .table {
          width: 100%;
        }
        .table .header {
          font-weight: bold;
        }
        .table > div {
          // display: grid;
          // grid-template-columns: 20% 20% 30% 30%;
          // grid-template-rows: 100vh;
        }
        .table > div span {
          flex-grow: 1;
          text-align: left;
        }
        `}
      </style>
    </>
  )
}

export default WarPerformance;
