import { ClanData, WarLog } from '../api'
import { Button } from "shards-react";
import moment from 'moment';

function filterMember(key) {
  return true
}

const WarParticipants = (props) => (
  <div>
    {props.name}
  </div>
)

const WarSeason = (props) => (
  <div>
    <h2>{props.seasonId}</h2>
    <h3>{moment(props.createdDate).fromNow()}</h3>
    {props.participants.map((d,i) => <WarParticipants key={i} {...d}></WarParticipants>)}
  </div>
)

const WarWinLoss = (props) => {
  var participants = []
  var findUser = (name) => participants.find((p) => p.name === name);

  props.items.forEach((war, i) => {
      console.log(`war ${i}`, war);
      war.participants.forEach(({name, battlesPlayed, wins}) => {
          var userObj = findUser(name)
          if (!!userObj) {
              userObj.battlesPlayed += battlesPlayed
              userObj.wins += wins
          } else {
              userObj = {name, battlesPlayed, wins}
              participants.push(userObj)
          }
      })
  })

  participants.sort((a, b) => b["wins"] - a["wins"] || a["battlesPlayed"] - b["battlesPlayed"])

  return participants.map(p => <div><span>Name: {p.name}</span><span>Wins: {p.wins}</span><span>Battles: {p.battlesPlayed}</span></div>)

}

const HomePage = (props) => (
  <>
    <WarWinLoss {...props.warlog}/>
  </>
)

HomePage.getInitialProps = async function() {
  const warlog = await WarLog()
  return { warlog }
}

export default HomePage
