//   let participants = []
//   const findUser = (name) => participants.find((p) => p.name === name);
//   items.forEach((war, i) => {
//       war.participants.forEach((user) => {
//           var userObj = findUser(user.name)
//           if (!!userObj) {
//               userObj.addToKey('battles', user.battlesPlayed)
//               userObj.addToKey('wins', user.wins)
//               userObj.addToKey('cards', user.cardsEarned)
//               userObj.addToKey('totalBattles', user.numberOfBattles)
//           } else {
//               userObj = new WarUser(user)
//               participants.push(userObj)
//           }
//       })
//   })
//   return participants
// }
//
  const sortData = (items, key, alt) => {
    return items.sort((a, b) => alt ? a[key] - b[key] : b[key] - a[key])
  }

const ClanTable = (props) => {

  const flatMemberList = props.memberList.forEach((m) => Object.keys(m).reduce((r, k) => r.concat(k, m[k]), []));
  console.log(flatMemberList)
  const [sort, setSort] = useState('score')
  const [alt, setAlt] = useState(false)
  const [data, setData] = useState(flatMemberList)

  const sortedData = sortData(data, sort, alt)

  const clickSort = (newSort) => {
    if (newSort === sort) {
      setAlt(!alt)
    } else {
      setSort(newSort)
    }
  }

  return (
    <>
      <div className="table-responsive">
        <table className={'table table-sm'}>
          <thead>
            <tr>
              {Object.keys(data[0]).map(k => <th className={sort === k ? 'active' : null} onClick={(() => clickSort(k))}>{k}</th>)}
            </tr>
          </thead>
          <tbody>
            {sortedData.map((p, i) => {
              // let className = p.score < -1 ? 'bg-danger' : p.score <= 1 ? 'table-danger' : p.score >= 8 ? 'table-warning' : 'table-success'
              return (
                <tr key={i}>
                  {Object.keys(p).map((k) => <td>{p[k]}</td>)}
                </tr>
              )
            })}
          </tbody>
        </table>
      </div>
      <style jsx>{`
        .table-responsive {
          overflow-x: initial;
        }
        .table {
          width: 100%;
        }
        .table thead {
          font-weight: bold;
        }
        .table thead th {
          cursor: pointer;
          position: sticky;
          top: 0;
          background: white;
          min-width: 8rem;
        }
        .table thead th span {
          display: block;
          font-size: 0.5rem;
        }
        .table thead th.active {
          color: blue;
        }
        `}
      </style>
    </>
  )
}

export default ClanTable;
