const processData = (items) => {
  let participants = []
  const findUser = (name) => participants.find((p) => p.name === name);
  items.forEach((war, i) => {
      console.log(`war ${i}`, war);
      war.participants.forEach(({name, battlesPlayed, wins}) => {
          var userObj = findUser(name)
          if (!!userObj) {
              userObj.battlesPlayed += battlesPlayed
              userObj.wins += wins
          } else {
              userObj = {name, battlesPlayed, wins}
              participants.push(userObj)
          }
      })
  })

  return participants.sort((a, b) => b["wins"] - a["wins"] || b["battlesPlayed"] - a["battlesPlayed"])
}

const WarPerformance = (props) => {

  const data = processData(props.items)
  console.log(data)
  return (
    <div className={'table'}>
      {data.map((p, i) => (
        <div key={i}>
          <span>Name: {p.name}</span>
          <span>Wins: {p.wins}</span>
          <span>Battles: {p.battlesPlayed}</span>
        </div>
      ))}
      <style jsx>{`
        .table {
          width: 100%;
        }
        .table span {
          display: flex;
        }
      `}</style>
    </div>
  )
}

export default WarPerformance;
