import { ClanData, WarLog } from '../api'
import { Button } from "shards-react";
import moment from 'moment';

function filterMember(key) {
  return true
}

const WarParticipants = (props) => (
  <div>
    {props.name}
  </div>
)

const WarSeason = (props) => (
  <div>
    <h2>{props.seasonId}</h2>
    <h3>{moment(props.createdDate).fromNow()}</h3>
    {props.participants.map((d,i) => <WarParticipants key={i} {...d}></WarParticipants>)}
  </div>
)

const HomePage = (props) => (
  <>
    {props.data.items.map((d,i) => <WarSeason key={i} {...d}></WarSeason>)}
  </>
)

HomePage.getInitialProps = async function() {
  const data = await WarLog()
  return { data }
}

export default HomePage
