import { useState } from 'react'

class WarUser {
  constructor({name, wins, battlesPlayed, cardsEarned, numberOfBattles}) {
    this.name = name
    this.wins = wins
    this.battles = battlesPlayed
    this.totalBattles = numberOfBattles
    this.cards = cardsEarned
  }

  addToKey(key, value) {
    this[key] += value
  }

  get ratio() {
    return this.wins / this.battles || 0
  }
}

const processData = (items) => {
  let participants = []
  const findUser = (name) => participants.find((p) => p.name === name);
  items.forEach((war, i) => {
      war.participants.forEach((user) => {
          var userObj = findUser(user.name)
          if (!!userObj) {
              userObj.addToKey('battles', user.battlesPlayed)
              userObj.addToKey('wins', user.wins)
              userObj.addCards('cards', user.cardsEarned)
              userObj.addCards('totalBattles', user.numberOfBattles)

          } else {
              userObj = new WarUser(user)
              participants.push(userObj)
          }
      })
  })
  return participants
}

const sortData = (items, key, alt) => {
  return items.sort((a, b) => alt ? a[key] - b[key] : b[key] - a[key])
}

const WarPerformance = (props) => {

  console.log(props)

  const [sort, setSort] = useState('wins')
  const [alt, setAlt] = useState(false)
  const [data, setData] = useState(processData(props.items))

  const sortedData = sortData(data, sort, alt)

  const clickSort = (newSort) => {
    if (newSort === sort) {
      setAlt(!alt)
    } else {
      setSort(newSort)
    }
  }

  return (
    <>
      <table className={'table'}>
        <thead>
          <tr className={'header'}>
            <th>Name</th>
            <th onClick={(() => clickSort('wins'))}>Wins</th>
            <th onClick={(() => clickSort('battles'))}>Battles</th>
            <th onClick={(() => clickSort('ratio'))}>Ratio</th>
            <th onClick={(() => clickSort('cards'))}>Cards</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((p, i) => (
            <tr key={i}>
              <td>{p.name}</td>
              <td>{p.wins}</td>
              <td>{p.battles}</td>
              <td>{p.ratio}</td>
              <td>{p.cards}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <style jsx>{`
        .table {
          width: 100%;
        }
        .table .header {
          font-weight: bold;
        }
        .table > div {
          // display: grid;
          // grid-template-columns: 20% 20% 30% 30%;
          // grid-template-rows: 100vh;
        }
        .table > div span {
          flex-grow: 1;
          text-align: left;
        }
        `}
      </style>
    </>
  )
}

export default WarPerformance;
