import { useState } from 'react'

class WarUser {
  constructor({name, wins, battlesPlayed}) {
    this.name = name
    this.wins = wins
    this.battles = battlesPlayed
  }

  addWins(wins) {
    this.wins += wins
  }

  addBattles(battles) {
    this.battles +=  battles
  }

  get ratio() {
    return this.wins / this.battles
  }
}

const processData = (items) => {
  let participants = []
  const findUser = (name) => participants.find((p) => p.name === name);
  items.forEach((war, i) => {
      war.participants.forEach((user) => {
          var userObj = findUser(user.name)
          if (!!userObj) {
              userObj.addBattles(user.battlesPlayed)
              userObj.addWins(user.wins)
          } else {
              userObj = new WarUser(user)
              participants.push(userObj)
          }
      })
  })

  return participants.sort((a, b) => b["wins"] - a["wins"] || b["battlesPlayed"] - a["battlesPlayed"])
}

const WarPerformance = (props) => {

  const [sort, setSort] = useState(['wins'])
  const state = 'hello'
  console.log(state)

  const data = processData(props.items)
  console.log(data)
  return (
    <div className={'table'}>
      <div className={'header'}>
        <span>Name</span>
        <span>Wins</span>
        <span>Battles</span>
        <span onClick={(() => state = 'blll')}>Ratio</span>
      </div>
      {data.map((p, i) => (
        <div key={i}>
          <span>{p.name}</span>
          <span>{p.wins}</span>
          <span>{p.battlesPlayed}</span>
          <span>{p.wins / p.battlesPlayed}</span>
        </div>
      ))}
      <style jsx>{`
        .table {
          width: 100%;
        }
        .table .header {
          font-weight: bold;
        }
        .table > div {
          display: grid;
          grid-template-columns: 20% 20% 30% 30%;
          // grid-template-rows: 100vh;
        }
        .table > div span {
          flex-grow: 1;
          text-align: left;
        }
      `}</style>
    </div>
  )
}

export default WarPerformance;
